export const GifGrid = ({ category }) => {
  const [counter, setcounter] = useState(0);
  useEffect(()=>{
      getGifs();

  },[])
  const getGifs = async () => {
    const url =
      "https://api.giphy.com/v1/gifs/search?q=Rick+and+Morty&limit=10&api_key=OpFy0pXFYaKcI6HPeVvV1euUvzuxNDl9";

    const resp = await fetch(url);
    const { data } = await resp.json();

    const gifs = data.map((img) => {
      return {
        id: img.id,
        title: img.title,
        url: img.images?.downsized_medium.url,
      };
    });
    console.log(gifs);
  };

//   getGifs();

